import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [activeSection, setActiveSection] = useState("about");
  const [currentVideo, setCurrentVideo] = useState(0);

  const handleSectionClick = (section) => {
    setActiveSection(section);
    const sectionElement = document.getElementById(section);
    sectionElement?.scrollIntoView({ behavior: "smooth" });
  };

  const videos = [
    "https://www.youtube.com/embed/V0E_wyHMlso",
    "https://www.youtube.com/embed/SDfQE70NrCY",
    "https://www.youtube.com/embed/mrHXRBsPtkM",
    "https://www.youtube.com/embed/tuN0Cs2XiEc",
  ];

  const handlePrevVideo = () => {
    setCurrentVideo((prev) => (prev === 0 ? videos.length - 1 : prev - 1));
  };

  const handleNextVideo = () => {
    setCurrentVideo((prev) => (prev === videos.length - 1 ? 0 : prev + 1));
  };

  useEffect(() => {
    const handleScroll = () => {
      const sections = ["about", "challenges", "solutions", "futureScope", "exploreMore"];
      sections.forEach((section) => {
        const element = document.getElementById(section);
        if (element && window.scrollY >= element.offsetTop - 50) {
          setActiveSection(section);
        }
      });
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="App">
      <h1>Water Footprint Awareness</h1>

      <nav>
        {["about", "challenges", "solutions", "futureScope", "exploreMore"].map((section) => (
          <button
            key={section}
            onClick={() => handleSectionClick(section)}
            className={activeSection === section ? "active-btn" : ""}
          >
            {section
              .split(/(?=[A-Z])/)
              .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
              .join(" ")}
          </button>
        ))}
      </nav>

      {/* About Section */}
      <section id="about" className={activeSection === "about" ? "active" : ""}>
        <h2>What is Water Footprint?</h2>
        <div style={{ display: "flex", alignItems: "center" }}>
          <p style={{ flex: 1 }}>
            The water footprint is a modern and comprehensive method for measuring the total volume of freshwater used to produce goods and services, both directly and indirectly. It offers a broader perspective than just the water we see flowing from taps or rivers, by accounting for the entire lifecycle of a product or service. This includes the rainwater absorbed by crops (known as green water), the surface and groundwater extracted for irrigation, industrial processes, or household use (blue water), and even the amount of clean freshwater required to absorb and dilute pollutants produced along the way (grey water).
          </p>
          <img
            src={require('./water_footprint.png')}
            alt="Water footprint illustration"
            className="section-img"
            style={{ width: "120px", height: "250px", marginLeft: "20px", marginRight: "20px" }}
          />
        </div>
      </section>

      {/* Challenges Section */}
      <section id="challenges" className={activeSection === "challenges" ? "active" : ""}>
        <h2>Challenges of Excessive Water Usage</h2>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img
            src={require('./water_crisis.jpg')}
            alt="Water scarcity and pollution"
            className="section-image"
            style={{ width: "270px", height: "270px", marginRight: "20px" }}
          />
          <ul style={{ flex: 1 }}>
            <li>🌊 <strong>Excessive Consumption:</strong> Excessive consumption of freshwater resources driven by agriculture, industrial processes, and everyday household use puts immense strain on available water supplies.</li>
            <li>⚠️ <strong>Hidden Water Usage:</strong> Hidden water usage embedded in the supply chain — many people remain unaware of the vast amounts of water required to produce everyday items, from clothing and food to electronics.</li>
            <li>🏭 <strong>Pollution Impact:</strong> High levels of pollution raise the grey water footprint, meaning even more clean water is needed to dilute and neutralize contaminants before the water can safely return to the environment.</li>
          </ul>
        </div>
        <div style={{ display: "flex", alignItems: "center" }}>
          <ul style={{ flex: 1 }}>
            <li>🌡️ <strong>Climate Change:</strong> Climate change, rapid population growth, and ineffective water management systems are intensifying water scarcity and amplifying stress on global freshwater resources.</li>
            <li>🐟 <strong>Ecological Disruption:</strong> Over-extraction and contamination of rivers, lakes, and groundwater disrupt aquatic ecosystems, threatening biodiversity and the health of natural habitats.</li>
          </ul>
        </div>
      </section>

      {/* Solutions Section */}
      <section id="solutions" className={activeSection === "solutions" ? "active" : ""}>
        <h2>Solutions to Reduce Water Footprint</h2>
        <div style={{ display: "flex", alignItems: "center" }}>
          <ul style={{ flex: 1 }}>
            <li>💧 <strong>Water-Efficient Agriculture:</strong> Embracing water-efficient technologies in agriculture — such as drip irrigation, precision farming, and the cultivation of drought-resistant crop varieties — can significantly reduce freshwater consumption while maintaining productivity.</li>
