// src/components/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';  // Import Link from React Router for navigation

const Navbar = () => {
  // Track which button is active
  const [activeButton, setActiveButton] = useState('about');  // Default is "about"

  // Function to handle button click and update the active button
  const handleButtonClick = (section) => {
    setActiveButton(section);
  };

  return (
    <nav>
      {/* Button for "About" section */}
      <button
        onClick={() => handleButtonClick('about')}  // Update active button when clicked
        className={activeButton === 'about' ? 'active-btn' : ''}  // Apply active class if selected
      >
        <Link to="/about" style={{ textDecoration: 'none', color: 'white' }}>About</Link>
      </button>

      {/* Button for "Challenges" section */}
      <button
        onClick={() => handleButtonClick('challenges')}
        className={activeButton === 'challenges' ? 'active-btn' : ''}
      >
        <Link to="/challenges" style={{ textDecoration: 'none', color: 'white' }}>Challenges</Link>
      </button>

      {/* Button for "Solutions" section */}
      <button
        onClick={() => handleButtonClick('solutions')}
        className={activeButton === 'solutions' ? 'active-btn' : ''}
      >
        <Link to="/solutions" style={{ textDecoration: 'none', color: 'white' }}>Solutions</Link>
      </button>

      {/* Button for "Future Scope" section */}
      <button
        onClick={() => handleButtonClick('futureScope')}
        className={activeButton === 'futureScope' ? 'active-btn' : ''}
      >
        <Link to="/futureScope" style={{ textDecoration: 'none', color: 'white' }}>Future Scope</Link>
      </button>

      {/* Button for "Explore More" section */}
      <button
        onClick={() => handleButtonClick('exploreMore')}
        className={activeButton === 'exploreMore' ? 'active-btn' : ''}
      >
        <Link to="/exploreMore" style={{ textDecoration: 'none', color: 'white' }}>Explore More</Link>
      </button>
    </nav>
  );
};

export default Navbar;
